function difficultyRating() {
  const e = document.querySelectorAll(".alakazam-rating-check");
  for (let a = 0; a < e.length; a++) {
    const t = e[a];
    t.parentElement.addEventListener("click", () => {
      t.checked
        ? t.parentElement.classList.toggle("alakazam-input-checkbox__checked")
        : t.parentElement.classList.remove("alakazam-input-checkbox__checked");
    });
  }
}
function mobileFilterDropdowns(e) {
  document.querySelectorAll(".alakazam-facet-filters").forEach(function (a) {
    a.id == e
      ? ((a.style.display = "none" == a.style.display ? "block" : "none"),
        a.classList.add("alakazam-facets__mobile-facets__item-active"),
        a.previousElementSibling.classList.toggle(
          "alakazam-facets__mobile-facets__heading-active"
        ))
      : ((a.style.display = "none"),
        a.classList.remove("alakazam-facets__mobile-facets__item-active"),
        a.previousElementSibling.classList.remove(
          "alakazam-facets__mobile-facets__heading-active"
        ));
  });
}
difficultyRating(), mobileFilterDropdowns();
